mapper.select:

select
o.*
from orderreservation o,generaluser g
where o.userid = g.userid

  and o.orderCode LIKE '%${ordercode}%'


  and o.logisticCode LIKE '%${logisticcode}%'


  and o.state = #{state,jdbcType=VARCHAR}


  and o.reserveState = #{reservestate,jdbcType=VARCHAR}


  and o.goodsName = #{goodsname,jdbcType=VARCHAR}


  and o.date >= #{startDate,jdbcType=VARCHAR}


  and o.date <= #{endDate,jdbcType=VARCHAR}


  and o.fetchDate >= #{startFetchDate,jdbcType=VARCHAR}


  and o.fetchDate <= #{endFetchDate,jdbcType=VARCHAR}

order by o.date DESC


select
o.*
from orderreservation o,generaluser g
where o.userid = g.userid

  and o.orderCode LIKE '%${ordercode}%'


  and o.logisticCode LIKE '%${logisticcode}%'


  and o.state = #{state,jdbcType=VARCHAR}


  and o.reserveState = #{reservestate,jdbcType=VARCHAR}


  and o.goodsName = #{goodsname,jdbcType=VARCHAR}

and g.userid = #{userid,jdbcType=INTEGER}

  and o.date >= #{startDate,jdbcType=VARCHAR}


  and o.date <= #{endDate,jdbcType=VARCHAR}


  and o.fetchDate >= #{startFetchDate,jdbcType=VARCHAR}


  and o.fetchDate <= #{endFetchDate,jdbcType=VARCHAR}


order by o.date DESC


select state,reserveState
from orderreservation
where id = #{id,jdbcType=INTEGER}
